css:
body, div, table, td, th, p {
  font-family: Arial, sans-serif;
  font-size: 12px;
}

@media print {
  @page {
    size: A4 portrait;
    margin: 0mm;
  }
  body {
    margin: 0;
    padding: 0;
  }
}

h2 {
  font-size: 20px;
  margin: 0;
  font-weight: bold;
}

h3 {
  font-size: 16px;
  margin: 0;
  font-weight: bold;
}

th {
  font-weight: bold;
  color: #000;
  font-size: 12px;
  background-color: #f5f5f5;
}

td, th {
  padding: 4px 6px;
  border: 1px solid #000;
}

.half-a4 {
  width: 100%;
  height: 148mm;
  padding: 15px 20px;
  box-sizing: border-box;
}

.do-print-wrap {
  border: 1px solid #000;
  padding: 0mm;
  margin: 0mm;
  width: 100%;
  box-sizing: border-box;
}

.print-format {
  padding: 5px 5px 5px 5px !important;
}

.print-format-preview {
  padding: 1px 1px 1px 1px;
}

.signature-section {
  position: absolute;
  bottom: 15px;
  left: 20px;
  right: 20px;
  border-top: 1px solid #000;
  padding-top: 10px;
}

.signature-row {
  display: flex;
  justify-content: space-between;
  margin-bottom: 15px;
}

.signature-item {
  width: 18%;
  text-align: center;
}

.signature-line {
  border-bottom: 1px solid #000;
  margin-top: 5px;
  height: 20px;
}

